import React, { Component } from 'react';
import { Route,BrowserRouter as Router,Link,Switch} from 'react-router-dom';
import styled from 'styled-components';
import true_trans from '../Dashboard/true_trans.png';
import Add_company from './Add_company/Add_company';
import Profile from './Profile/Profile';


const Dropbtn = styled.button`
    font-size: 16px;  
    border: none;
    outline: none;
    color: black;
    padding: 14px 16px;
    background-color: #ffffff;
    font-family: inherit;
    margin: 0;
    &:hover{
      background-color: #ddd;
     
      border-bottom: 3px solid #ff5722;
    }
`;

const Header = styled.div`
  overflow: hidden;
  background-color: #ffffff;
  padding: 15px;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  & a{
    float: right;
    font-size: 16px;
    color: black;
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
    
  }
  & a:hover{
    background-color: #ddd;
    display:block;
    text-decoration: none;
    border-bottom: 3px solid #ff5722;
  
  }
  // &:hover ${Dropbtn} {
  //   background-color: #ddd;
  // }


`;
const Dropdown_Content = styled.div`
  display: none;
  position: absolute;
  background-color: #f9f9f9;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  z-index: 1;

  & a{
    float: none;
    color: black;
    padding: 12px 16px;
    text-decoration: none;
    display: block;
    text-align: left;
    cursor:pointer;
  }
`;

const Dropdown = styled.div`
    float: right;
    overflow: hidden;
    &:hover ${Dropdown_Content} {
      display: block;
      text-decoration: none;
    
    & a:hover{
      background-color: #ddd;
    }

`;

const Content = styled.div`
  box-shadow: 0px 0px 5px 6px rgba(0, 0, 0, 0.06);
  padding : 10px;
  margin : 10px;
  height: 100%;
`;
const HomePage_tittle = styled.div`
    text-align: center;
    color: cornflowerblue;
`;
const Company_Button = styled.button`
&a{
  text-decoration: none;
}
`;
const Addcompany = styled.div`
text-align: center;
`;

class Dashboard extends Component {

  constructor(props) {
  
    super(props);
    this.state = {
        company_id:'',
        user_type:''
    }

    this.logout=this.logout.bind(this);
  }
  logout(){
    sessionStorage.removeItem('formData');
    localStorage.removeItem('formData');
    this.props.history.push("/");
  }

 
    
  render() {
    const sessionData = localStorage.getItem('formData');
    
    const AllFormData = JSON.parse(sessionData);
    
    const Company_id = AllFormData.data.data.tpsData.company_id;
      
    const User_type = AllFormData.data.data.tpsData.user_type;
   

    return (
     
      <div className="main_dashboard">
      
     
         <Router basename='/'>
           <div>
           <Header>
           <img src={true_trans} alt="true_trans" />
                <Dropdown>
                  <Dropbtn>Profile 
                    &nbsp;  <i className="fa fa-caret-down"></i>
                  </Dropbtn>
                  <Dropdown_Content>
                    <Link to="/profile">Profile</Link>
                    <a onClick={this.logout}>Logout</a>
                    
                  </Dropdown_Content>
                </Dropdown> 
                <a href="#home">Home</a>
               
            </Header>

            <Content>
           
              <HomePage_tittle>
                <h1>Welcome to TrueTrans</h1>
              </HomePage_tittle>
              {Company_id === "" && User_type === "Admin" ?
              <AddCompany/>:""}
            <div>
              <Route path="/add_company" component={Add_company}/>
              <Route path="/profile" component={Profile}/>
            </div>
               
            </Content>


           </div> 
         </Router>
        </div>  
        

     
    );
  }
 
}

class AddCompany extends Component{
  constructor(props){
    super(props);
  }

  Add_company(){
    
      this.props.history.push('/hello');
  
  }


  render(){
 
    
    return(
      <Addcompany>
        
           <Company_Button className="btn btn-success" onClick={this.Add_company}><Link to='/add_company'>+ Add Company</Link></Company_Button>
        
      </Addcompany>
    );
  }
}


